import React from 'react';


export class TodoForm extends React.Component {
    constructor() {
        super();
        this.addTask = this.addTask.bind(this);
    }

    addTask(e) {
        e.preventDefault();
        var inp = document.getElementById('todoInput');
        var val = inp.value;
        inp.value = '';
        this.props.addTask(val);
        this.state.selected = ''
    }

    render() {
        return (
            <div>
                <div className="todo type1">
                    <form className="input-wrapper" onSubmit={this.addTask}>
                        <input id="todoInput" type="text" className="add-todo" name="add-todo" placeholder="やるべき事?" />
                        <span>レベル</span>
                        <div>
                            <input type='radio' id='radio-1' name='myRadio' value='radio-1'
                                checked={this.state.selected === 'radio-1'} onChange={(e) => this.setState({ selected: e.target.value })} />
                            <br />
                            <input type='radio' id='radio-2' name='myRadio' value='radio-2'
                                checked={this.state.selected === 'radio-2'} onChange={(e) => this.setState({ selected: e.target.value })} />
                        </div>
                    </form>
                </div>
                <button type="button" onClick={this.addTask} className="add-btn" />
            </div>
        )
    }
}
